fill((10,0)--(7.5,5)--(10,10)--cycle,blue);
draw((0,0)--(10,0));
draw((10,0)--(10,10));
draw((10,10)--(0,10));
draw((0,0)--(0,10));
draw((0,0)--(5,10));
draw((5,10)--(10,0));
draw((0,10)--(5,0));
draw((5,0)--(10,10));
fill((5,0)--(7.5,5)--(5,10)--(2.5,5)--cycle,gray);
